import React, {useReducer} from 'react';
import uuid from 'uuid';
import TaskContext from './taskContext';
import TaskReducer from './taskReducer';
import { PROJECT_TASKS,
         NEW_TASK, 
         VALIDATE_TASK, 
         DELETE_TASK,
         TASK_STATUS,
         ACTUAL_TASK,
         UPDATE_TASK
        } from '../../types';

const TaskState = props => {

    const initialState = {
        projectTasks: null,
        taskError: false, 
        actualTask: null
    }

    const [state, dispatch] = useReducer(TaskReducer, initialState);

    const getProjectTasks = projectId => {
        dispatch({
            type: PROJECT_TASKS,
            payload: projectId
        })
    }

    const newTask = task => {
        task.id = uuid.v4();
        dispatch({
            type: NEW_TASK,
            payload: task
        })
    }

    const validateTask = () => {
        dispatch({
            type: VALIDATE_TASK
        })
    }

    const deleteTask = id => {
        dispatch({
            type: DELETE_TASK,
            payload: id
        })
    }

    const changeTaskStatus = task => {
        dispatch({
            type: TASK_STATUS,
            payload: task
        })
    }

    const getActualTask = task => {
        dispatch({
            type: ACTUAL_TASK,
            payload: task
        })
    }

    const updateTask = task => {
        dispatch({
            type: UPDATE_TASK,
            payload: task
        })
    }

    return (
        <TaskContext.Provider
            value={{
                projectTasks: state.projectTasks,
                taskError: state.taskError,
                actualTask: state.actualTask,
                getProjectTasks,
                newTask,
                validateTask,
                deleteTask,
                changeTaskStatus,
                getActualTask,
                updateTask
            }}
        >
            {props.children}
        </TaskContext.Provider>
    )

}

export default TaskState;
