import { 
    SUCCESSFULL_LOGIN,
    SUCCESSFULL_SIGNIN,
    FAILED_LOGIN,
    FAILED_SIGNIN,
    GET_USER,
    LOG_OUT
 } from '../../types';

export default (state, action) => {
    switch(action.type) {

        case SUCCESSFULL_SIGNIN:
            localStorage.setItem('token', action.payload.token);    
            return {
                ...state,
                auth: true,
                msg: null    
            }

        case FAILED_LOGIN:
        case FAILED_SIGNIN:
            localStorage.removeItem('token');
            return {
                ...state,
                token: null,
                msg: action.payload    
            }

        case GET_USER:
            return {
                ...state,
                user: action.payload
            }

        default:
            return state;
    }
}
