import React, {useReducer} from 'react';
import TaskContext from './taskContext';
import TaskReducer from './taskReducer';
import {} from '../../types';

const TaskState = props => {

    const initialState = {
        tasks : [
        { name: 'Elegir plataforma', status: false, projectId: 1},
        { name: 'Elegir dise単o', status: true, projectId: 1},
        { name: 'Elegir hosting', status: false, projectId:3},
        { name: 'Elegir colores', status: true, projectId: 2},
        { name: 'Elegir plataforma', status: false, projectId: 1},
        { name: 'Elegir dise単o', status: true, projectId: 3},
        { name: 'Elegir hosting', status: false, projectId:3},
        { name: 'Elegir colores', status: true, projectId: 2},
        ]
    }

    const [state, dispatch] = useReducer(TaskReducer, initialState);

    return (
        <TaskContext.Provider
            value={{
                tasks: state.tasks
            }}
        >
            {props.children}
        </TaskContext.Provider>
    )

}

export default TaskState;
