import { PROJECT_TASKS, NEW_TASK } from '../../types';

export default (state, action) => {
    switch(action.type) {

        case PROJECT_TASKS:
            return {
                ...state,
                projectTasks: state.tasks.filter( task => task.projectId === action.payload)
            }

        case NEW_TASK:
            return {
                ...state,
                tasks: [...tasks, action.payload]
            }


        default:
            return state;
    }
}
